import './App.css';
import {useState} from 'react';

function App() {
  const [description, setDescription] = useState('Click on a point to view is data here');
  const [active, setActive] = useState(true)
  return (
    <div className="App">
      <header className='header'>
        <p>GMU History Dept. Mapping Volience In Italy</p>
      </header>
      <div className="Map">
        <div id="italyImg"/>
            <button className='button' onClick={() => setDescription(jsonData)}>
            </button> 
        </div>    
      <div className='DataInfo'>
        <InfoBar isActive={active === true} desc={description}/>
      </div>
    </div>
  );
}

let jsonData = {
  "Name" : "Chris",
  "Location" : "Venice",
  "Weapon" : "Gold Crossbow"
}

function InfoBar({desc}) {
  return (
    <div classname="InfoBar">
        <p>{JSON.stringify(description)}</p>
    </div>
  );
}



//<p>The description is {description}</p>
